var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.advertisements = new Datastore({
  filename: "storage/advertisements.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});
db.advertisements.ensureIndex({fieldName: 'advertisementName', unique:true}, function(err) {});
db.advertisements.loadDatabase();
db.advertisements.ready = ready.promise;
module.exports = db.advertisements;
