var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.placements = new Datastore({
  filename: "storage/placements.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.placements.loadDatabase();
db.placements.ready = ready.promise;
module.exports = db.placements;
